import styled from "styled-components";

export const FlexContainer = styled.main`
    display: flex;
    flex-direction: column;
    background-color: #1A1A1A;
    padding: 40px;

    #Categorias{
        padding: 35px;
        display: flex;
        flex-wrap: wrap;
        justify-content: space-between;
    }

    #ColocarCategoria{
        display: flex;
        justify-content: flex-end;
        width: 100%;
        background-color: #1A1A1A;
        border: transparent;
    }

    #ColocarCategoria img{
        width: 80px;
        height: 80px;
    }
`
