//importar os estilos da biblioteca de styled-components em styled
import styled from "styled-components";

//declara uma variavel para guardar a "classe" do CSS e exportar ela para o "APP"
export const FlexContainer = styled.header`
    display: flex;
    align-items: center;
    justify-content: space-between;
    background-color: #1A1A1A;
    width: 100%;
    height: 4.5rem;

    h1{
        margin:0;
    }

    img{
        height: 30px;
        width: 30px;
    }

    #Inicio{
        margin-left: 60px;
    }
    #Fim{
        margin-right: 60px;
    }

    ul{
        display: flex;
        gap: 30px;
        line-height: 21px;
        font-weight: 400;
        font-size: 25px;
        display: flex;
        list-style-type: none;
        margin: 0;
        padding: 0;

    }

    button{
        background-color: transparent;
         line-height: 21px;
        font-weight: 400;
        font-size: 25px;
    }

    li button:hover{
        padding-bottom: 5px;
        border-bottom: 3px solid #7CA431;
    }

    div{
        display: flex;
        gap: 15px;
    }
`
